frappe.ui.form.on('Stock Entry', {
     voucher_no: function(frm) {
        frappe.call({
			"method": "frappe.client.get",
			args: {
				doctype: "Stock Entry",
				name: frm.doc.voucher_no
			},
			callback: function(data){
				frm.clear_table('items');
				let items = data.message.items;

				for (var item in items) {
					var row = frm.add_child("items");
					row.item_code = items[item].item_code;
					row.qty = items[item].qty;
					row.uom = items[item].uom;
					row.stock_uom = items[item].stock_uom;
					row.basic_rate = items[item].basic_rate;
					row.transfer_qty = items[item].transfer_qty;
					row.conversion_factor = items[item].conversion_factor;

				}
				frm.refresh_field("items");
			}
		});

    },
});
